import React from "react";
import Bussiness_Sidebar from "../Bussiness_Sidebar";
import Navbar from "../Navbar";
import { useNavigate, useParams } from "react-router-dom";
import { useState } from "react";
import { useEffect } from "react";

const AddnewUserUpdates = () => {
  const navigate = useNavigate();
  const [title, setTitle] = useState();
  const [userfirst, setUserFirst] = useState();
  const [Userlast, setUserLast] = useState();
  const [UserEmail, setUserEmail] = useState();
  const [username, setUsername] = useState();
  const [Userpassword, setUserPassword] = useState();
  const [UsercPassword, setUserCPassword] = useState();
  const [UserRole, setUserRole] = useState();
  const [UserSalesCommision, setUserSalesCommission] = useState();
  const [UserMaxsalesDiscount, setUserMaxSalesDiscount] = useState();
  const [UserBirth, setUserBirth] = useState();
  const [USerGender, setUserGender] = useState();
  const [UserBloodGroup, setUserBloodGroup] = useState();
  const [UserNumber, setUserNumber] = useState();
  const [UserFamilyNumber, setUserFamilyNumber] = useState();
  const [UserAccountHolder, setUserAccountHolder] = useState();
  const [UserAccountNumber, setUserAccountNumber] = useState();

  const [UserBankName, setUserBankName] = useState();
  const [UserBankIdentifi, setUserBankIdentifi] = useState();
  const [UserBranch, setUserBranch] = useState();
  const [UserTaxPay, setUserTaxPay] = useState();
  const [error, setError] = useState(false);

  const params = useParams();
const userGender = (e) => {
    setUserGender(e.target.value);
  };

  useEffect(() => {
    getAllUserDetail(); }, []);

  const getAllUserDetail = async () => {
    console.warn(params);
    let result = await fetch(`http://localhost:8000/newuserupdates/${params.id}`);
    result = await result.json();
    console.log("result", result);
    setTitle(result.title);
    setUserFirst(result.userfirst);
    setUserLast(result.Userlast);
    setUserEmail(result.usere)

    setUsername(result.username);
    setUserPassword(result.Userpassword);
    setUserCPassword(result.UsercPassword)
    setUserRole(result.UserRole);
    

    setUserSalesCommission(result.UserSalesCommision);
    setUserSalesCommission(result.UserMaxsalesDiscount);
    setUserBirth(result.UserBirth)
    setUserGender(result.userGender);

    setUserBloodGroup(result.UserBloodGroup);
    setUserNumber(result.UserNumber);
    setUserFamilyNumber(result.UserFamilyNumber)

    setUserAccountHolder(result.UserAccountHolder);
    setUserAccountNumber(result.UserAccountNumber);
    setUserBankName(result.UserBankName)

    setUserBankIdentifi(result.UserBankIdentifi);
    setUserBranch(result.UserBranch);
    setUserTaxPay(result.UserTaxPay)
  };

  
 


  const UpdatenewUserHandler = async () => {

    // console.warn(hospitalname, hospitalAddress, hospitaldetail, hospitalstate, hospitalcity,)
    let result = await fetch(`http://localhost:8000/newuserupdates/${params.id}`, {
        method: "Put",
        body: JSON.stringify({
            title, userfirst,
            Userlast,
            UserEmail,
            username,
            Userpassword,
            UsercPassword,
            UserRole,
            UserSalesCommision,
            UserMaxsalesDiscount,
            UserBirth,
            USerGender,
            UserNumber,
            UserFamilyNumber,
            UserAccountHolder,
            
            UserBankName,
            UserBankIdentifi,
            UserBranch,
            UserTaxPay

           
        }),
        headers: {
            "Content-type": "application/json"
        }
    });
    result = await result.json();
    if (result) {
        // navigate('/all-hospital')
    }
}

  return (
    <>
      <div>
        <div>
          <div className="layout-wrapper layout-content-navbar">
            <div className="layout-container">
              <Bussiness_Sidebar />

              <div className="layout-page">
                <Navbar />
                <div className="content-wrapper">
                  <div className="container-xxl flex-grow-1 container-p-y">
                    <div className="row">
                      <div className="col-md-12">
                        <div className="card mb-4">
                          <h5 className="card-header">Update user</h5>
                          <div className="card-body"></div>
                          <hr className="my-0" />
                          <div className="card-body">
                            <div>
                              <div className="row">
                                <div className="mb-3 col-md-4">
                                  <label for="title" className="form-label">
                                    Prefix :
                                  </label>
                                  <input
                                    className="form-control"
                                    type="text"
                                    name="patientfirstName"
                                    value={title}
                                    onChange={(e) => {
                                      setTitle(e.target.value);
                                    }}
                                    placeholder="First specific here.."
                                    autofocus
                                  />
                                </div>
                                <div className="mb-3 col-md-4">
                                  <label
                                    for="patientfirstName"
                                    className="form-label"
                                  >
                                    First Name
                                  </label>
                                  <input
                                    className="form-control"
                                    type="text"
                                    name="patientfirstName"
                                    value={userfirst}
                                    onChange={(e) => {
                                      setUserFirst(e.target.value);
                                    }}
                                    placeholder="First name here.."
                                    autofocus
                                  />
                                </div>
                                <div className="mb-3 col-md-4">
                                  <label
                                    for="patientlastName"
                                    className="form-label"
                                  >
                                    Last Name
                                  </label>
                                  <input
                                    className="form-control"
                                    type="text"
                                    name="patientlastName"
                                    placeholder="Last name here.."
                                    value={Userlast}
                                    onChange={(e) => {
                                      setUserLast(e.target.value);
                                    }}
                                  />
                                </div>
                                <div className="mb-3 col-md-4">
                                  <label
                                    for="patientlastName"
                                    className="form-label"
                                  >
                                    Email :
                                  </label>
                                  <input
                                    className="form-control"
                                    type="email"
                                    name="patientlastName"
                                    placeholder="Enter your Email here.."
                                    value={UserEmail}
                                    onChange={(e) => {
                                      setUserEmail(e.target.value);
                                    }}
                                  />
                                </div>
                                <hr />

                                <div className=" mb-4">
                                  <h5 className="card-header">
                                    Role and Permission
                                  </h5>
                                  <div className="card-body"></div>
                                  <hr className="my-0" />
                                  <div className="card-body">
                                    <div className="row">
                                      <div className="mb-3 col-md-4">
                                        <label
                                          for="country"
                                          className="form-label"
                                        >
                                          Username
                                        </label>

                                        <input
                                          className="form-control"
                                          type="text"
                                          name="patientfirstName"
                                          value={username}
                                          onChange={(e) => {
                                            setUsername(e.target.value);
                                          }}
                                          placeholder="Enter your Username.."
                                          autofocus
                                        />
                                      </div>
                                      <div className="mb-3 col-md-4">
                                        <label
                                          for="state"
                                          className="form-label"
                                        >
                                          Password
                                        </label>

                                        <input
                                          className="form-control"
                                          type="password"
                                          name="patientfirstName"
                                          value={Userpassword}
                                          onChange={(e) => {
                                            setUserPassword(e.target.value);
                                          }}
                                          placeholder="Enter your password .."
                                          autofocus
                                        />
                                      </div>
                                      <div className="mb-3 col-md-4">
                                        <label
                                          for="city"
                                          className="form-label"
                                        >
                                          C Password
                                        </label>

                                        <input
                                          className="form-control"
                                          type="text"
                                          name="patientfirstName"
                                          value={UsercPassword}
                                          onChange={(e) => {
                                            setUserCPassword(e.target.value);
                                          }}
                                          placeholder="Enteer your Confirm password  here.."
                                          autofocus
                                        />
                                      </div>
                                      <div className="mb-3 col-md-5">
                                        <label
                                          for="address"
                                          className="form-label"
                                        >
                                          Role{" "}
                                        </label>
                                        <input
                                          className="form-control"
                                          type="text"
                                          name="address"
                                          placeholder="Enter your role"
                                          value={UserRole}
                                          onChange={(e) => {
                                            setUserRole(e.target.value);
                                          }}
                                        />
                                      </div>
                                    </div>
                                  </div>
                                </div>
                                <div className=" mb-4">
                                  <h5 className="card-header">Sales </h5>
                                  <div className="card-body"></div>
                                  <hr className="my-0" />
                                  <div className="card-body">
                                    <div className="row">
                                      <div className="mb-3 col-md-4">
                                        <label
                                          for="patientType"
                                          className="form-label"
                                        >
                                          Sales Commission Percentage (%):{" "}
                                        </label>

                                        <input
                                          className="form-control"
                                          type="text"
                                          name="patienttype"
                                          value={UserSalesCommision}
                                          onChange={(e) => {
                                            setUserSalesCommission(
                                              e.target.value
                                            );
                                          }}
                                          placeholder=" Enter Sales Comision here.."
                                          autofocus
                                        />
                                      </div>
                                      <div className="mb-3 col-md-4">
                                        <label
                                          for="consultant"
                                          className="form-label"
                                        >
                                          Max sales discount percent:{" "}
                                        </label>

                                        <input
                                          className="form-control"
                                          type="text"
                                          name="patientfirstName"
                                          value={UserMaxsalesDiscount}
                                          onChange={(e) => {
                                            setUserMaxSalesDiscount(
                                              e.target.value
                                            );
                                          }}
                                          placeholder="Max sales Discout  here.."
                                          autofocus
                                        />
                                      </div>
                                    </div>
                                  </div>
                                </div>
                                <div className=" mb-4">
                                  <h5 className="card-header">
                                    More information
